import { eventBus, CLEAR_FILEDS } from "../../../services/EventBusService.js";

export default {
  data() {
    return {
      options: [
        {
          value: "Israeli"
        },
        {
          value: "French"
        },
        {
          value: "Italian"
        },
        {
          value: "British"
        },
        {
          value: "Vietnamese"
        },
        {
          value: "Chinese"
        },
        {
          value: "Indian"
        },
        {
          value: "Other"
        }
      ],
      value: ""
    };
  },
  created() {
    eventBus.$on("CLEAR_FILEDS", () => {
      this.value = '';
    });
  },
  methods: {
    onSave() {
      this.$emit("cuisine", this.value);
    },
    clearSelect() {
      this.value = "";
      this.onSave();
    }
  }
};
